{"changed":false,"filter":false,"title":"facturas_controller.rb","tooltip":"/app/controllers/facturas_controller.rb","value":"include UsersHelper\ninclude CustomersHelper\ninclude ServicesHelper\nrequire \"open-uri\"\n \nclass FacturasController < ApplicationController\n  \n    $: << Dir.pwd  + '/lib'\n\n # before_filter :authenticate_user!\n \n \n \n\n  def reportes\n  \n    @company=Company.find(1)          \n    @fecha = params[:fecha1]    \n    \n    @parte_rpt = @company.get_parte_1(@fecha)\n    \n    \n    case params[:print]\n      when \"To PDF\" then \n        begin \n         render  pdf: \"Ordenes \",template: \"varillajes/parte_rpt.pdf.erb\",locals: {:varillajes => @parte_rpt}\n        \n        end   \n      when \"To Excel\" then render xlsx: 'exportxls'\n      else render action: \"index\"\n    end\n  end\n  \n  def reportes2 \n  \n    @company=Company.find(1)          \n    @fecha = params[:fecha1]    \n    \n    @parte_rpt = @company.get_parte_1(@fecha)\n    \n    \n    case params[:print]\n      when \"To PDF\" then \n        begin \n         render  pdf: \"Ordenes \",template: \"varillajes/parte2_rpt.pdf.erb\",locals: {:varillajes => @parte_rpt}\n        \n        end   \n      when \"To Excel\" then render xlsx: 'exportxls'\n      else render action: \"index\"\n    end\n  end\n  \n  def reportes3 \n  \n    @company=Company.find(1)          \n    @fecha1 = params[:fecha1]    \n    @fecha2 = params[:fecha2]    \n    \n    @contado_rpt = @company.get_parte_2(@fecha1,@fecha2)\n    \n    \n    case params[:print]\n      when \"To PDF\" then \n        begin \n         render  pdf: \"Ordenes \",template: \"varillajes/parte3_rpt.pdf.erb\",locals: {:varillajes => @contado_rpt}\n        \n        end   \n      when \"To Excel\" then render xlsx: 'exportxls'\n      else render action: \"index\"\n    end\n  end\n\ndef reportes4 \n    $lcFacturasall = '1'\n\n    @company=Company.find(1)          \n    @fecha1 = params[:fecha1]    \n    @fecha2 = params[:fecha